function forNumber(e){document.getElementById("result").value+=e}function forOperator(e){var l=document.getElementById("result");"+"==l.value.slice(-1)||"-"==l.value.slice(-1)||"*"==l.value.slice(-1)||"/"==l.value.slice(-1)?l.value=l.value.slice(0,-1)+e:""==l.value||"."==l.value.slice(-1)||(l.value+=e)}function forClear(){document.getElementById("result").value=""}function forEqual(){var result=document.getElementById("result");"+"==result.value.slice(-1)||"-"==result.value.slice(-1)||"*"==result.value.slice(-1)||"/"==result.value.slice(-1)||""==result.value||"."==result.value.slice(-1)||(result.value=eval(result.value))}